package sign

import (
	"bytes"
	"encoding/base64"
	"fmt"
	"testing"
)

func TestDecode(t *testing.T) {
	bca := []byte("")
	ca := bytes.Buffer{}
	_, err := base64.StdEncoding.DecodeString(string(bca))
	if err != nil {
		panic(fmt.Sprintf("decode ca fail: %s", err.Error()))
	}
	fmt.Printf("%s", ca.Bytes())
}
